fn try_cast_slice_box<A, B>(Box<[A]>)
-> Result<Box<[B]>, (PodCastError, Box<[A]>)> where A: NoUninit,
B: AnyBitPattern

enum CheckedCastError {
    PodCastError(crate::PodCastError),
    InvalidBitPattern,
}

fn try_cast_slice_mut<A, B, '_>(&'_ mut [A])
-> Result<&mut [B], CheckedCastError> where A: NoUninit + AnyBitPattern,
B: CheckedBitPattern + NoUninit

enum PodCastError {
    TargetAlignmentGreaterAndInputNotAligned,
    OutputSliceWouldHaveSlop,
    SizeMismatch,
    AlignmentMismatch,
}
