enum ErrorVariant<R> {
    ParsingError {
            positives: Vec<R>,
            negatives: Vec<R>,
        },
    CustomError {
            message: String,
        },
}

enum LineColLocation {
    Pos((usize, usize)),
    Span((usize, usize), (usize, usize)),
}

enum Token<'i, R> {
    Start {
            rule: R,
            pos: Position,
        },
    End {
            rule: R,
            pos: Position,
        },
}
